                if (boost[i] < 0) {
                    delete boost[i];
                    showMsg = true;
                }
            }
            if (showMsg && !effect.secondaries && effect.id !== 'octolock') {
                this.add('-fail', target, 'unboost', '[from] item: Clear Amulet', `[of] ${target}`);
            }
        }

