            if (effect?.name === 'Opportunist' || effect?.name === 'Mirror Herb')
                return;
            if (!this.effectState.boosts)
                this.effectState.boosts = {};
            const boostPlus = this.effectState.boosts;
            let i;
            for (i in boost) {
                if (boost[i] > 0) {
                    boostPlus[i] = (boostPlus[i] || 0) + boost[i];
                    this.effectState.ready = true;
                }
            }
        }

